import useFetch from "./useFetch";
import NoteList from "./NoteList";

const Home = () => {
  const { error, isPending, data: notes } = useFetch('http://localhost:8000/notes')
  return (
    <div className="home">
      { error && <div> {error} </div>}
      { isPending && <div> loading ...</div>}
      { notes && <NoteList notes= {notes} />}
    </div>
  );
}

export default Home;
