import { useState, useEffect } from 'react';
import { collection, getDocs, doc, getDoc } from 'firebase/firestore';
import { firestore } from './index'; // Path to your index.js file

const useFetch = (id = 'defaultValue') => {
  const [notes, setNotes] = useState([]);
  const [isPending, setIsPending] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchNotes = async () => {
      try {
        if (id !== 'defaultValue') {
          const collectionName = 'Notes';
          const documentId = id;
          const documentRef = doc(firestore, collectionName, documentId);

          // Retrieve the document data
          const docSnapshot = await getDoc(documentRef);

          if (docSnapshot.exists()) {
            const data = docSnapshot.data();
            setNotes([data]);
          } else {
            console.log('Document not found!');
            setNotes([]);
          }
        } else {
          const notesSnapshot = await getDocs(collection(firestore, 'Notes'));
          const fetchedNotes = notesSnapshot.docs.map((doc) => doc.data());
          setNotes(fetchedNotes);
        }

        setIsPending(false);
        setError(null);
      } catch (error) {
        console.error('Error fetching notes: ', error);
        setIsPending(false);
        setError(error.message);
      }
    };

    fetchNotes();
  }, [id]);

  return { notes, isPending, error };
};

export default useFetch;
