import { useState} from "react";
import { useNavigate } from "react-router-dom";
import { collection, addDoc } from 'firebase/firestore';
import { firestore } from './index';

const Create = () => {
  const [title, setTitle] = useState('');
  const [body, setBody] = useState('');
  const [author, setAuthor] = useState('mario');
  const [isPending, setIsPending] = useState(false)
  const navigate = useNavigate();
  const handleSubmit = async (e) => {
    e.preventDefault();
    const newNote = { title, body, author };
    setIsPending(true);
    try {
      const collectionName = 'Notes';
      const docRef = await addDoc(collection(firestore, collectionName), {
        id: docRef.id, // Add the document ID as a field
        title,
        body,
        author
      });
      console.log('Document added with ID:', docRef.id);
      setIsPending(false);
      navigate('/'); // Redirect to the home page or the desired route after adding the note
    } catch (error) {
      console.error('Error adding document:', error);
      setIsPending(false);
    }
  };

  return (
    <div className="create">
      <h2>Add a new note</h2>
      <form onSubmit={handleSubmit}>
        <label>Note Title:</label>
        <input type="text" required value={title} onChange={(e) => setTitle(e.target.value)}/>
        <label>Note Body:</label>
        <textarea required value={body} onChange={(e) => setBody(e.target.value)}></textarea>
        <label>Note Author:</label>
        <select value={author} onChange={(e)=>setAuthor(e.target.value)}>
          <option value="mario">mario</option>
          <option value="yoshi">yoshi</option>
        </select>
        {!isPending && <button>Add NOTE</button>}
        {isPending && <button disabled>Adding NOTE...</button>}
      </form>
    </div>
  );
}

export default Create;
