import { useState, useEffect } from "react";
import { collection, getDocs } from 'firebase/firestore';
import { firestore } from './index'; // Path to your index.js file

const useFetch = (id = 'defaultValue') => {
  const [notes, setNotes] = useState([]);
  const [isPending, setIsPending] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchNotes = async () => {
      try {
        if(id){
          const notesSnapshot = collection(firestore, 'Notes');
          console.log(notesSnapshot)
          // const fetchedNotes = notesSnapshot.docs.map(doc => console.log(doc.id, ' => ', doc.data()));
          // setNotes(fetchedNotes);
          setIsPending(false);
          setError(null);

        }else{
          const notesSnapshot = await getDocs(collection(firestore, 'Notes'));
          const fetchedNotes = notesSnapshot.docs.map(doc => doc.data());
          console.log(fetchedNotes)
          setNotes(fetchedNotes);
          setIsPending(false);
          setError(null);
        }
      } catch (error) {
        console.error('Error fetching notes: ', error);
        setIsPending(false);
        setError(error.message);
      }
    };

    fetchNotes();
  }, []);

  return { notes, isPending, error };
}

export default useFetch;
